import React, { useState, useEffect } from "react";
import  "./Post.css";
import Modal from './../../common/Modal/Modal';
import Sidebar from "../../components/Sidebar/Sidebar";
import Axios from "axios";

function Home(props) {
  const [showModal, setShowModal] = useState(false);
  // const [showDetails, setShowDetails] = useState(false);
  const [postTitle, setPostTitle] = useState("");
  const [postDescription, setPostDescription] = useState("");
  const [scheduleDate, setScheduleDate] = useState("");
  const [formComplete, setFormComplete] = useState(false);
  const [formIncompleteError, setFormIncompleteError] = useState(false);

  const [postName, setPostName] = useState("");
  const [postDetail, setPostDetail] = useState("");
  const [postDate, setPostDate] = useState("");

  const [postData, setPostData] = useState("");
  const [isPostAdded, setIsPostAdded] = useState(false);
  const [isPostDelete, setIsPostDelete] = useState(false);
  const [isPostEdited, setIsPostEdited] = useState(false);

  const postTitles  = React.useRef();
  const postDescriptions  = React.useRef();
  const scheduleDates  = React.useRef();


    // to Hide past date
    var today = new Date();
    var month,
      day = null;
    if (today.getMonth() < 10 || today.getDate() < 10) {
      month = "0" + (today.getMonth() + 1);
      day = "0" + today.getDate();
    }
    var date = today.getFullYear() + "-" + month + "-" + day;

    useEffect(() => {
      setIsPostAdded(false);
      setIsPostDelete(false);
      setIsPostEdited(false);
      getPostData();
    }, [isPostAdded, isPostDelete, isPostEdited]);

    const getPostData = () => {
      Axios
      .get(`https://react-crud-e02cf.firebaseio.com/post.json`)
      .then((response) => {
        setPostData(response.data);
      })
      .catch((error) => console.log(error));
  };

//  to Add and Edit cardData in DB
const handleAddPostData = (e) => {
  //   check if all input is taken
  if(postTitles.current.value.length === 0 || postDescriptions.current.value.length === 0  || scheduleDates.current.value.length === 0 ){
    setFormComplete(false);
    setFormIncompleteError(true);
  } else {
    // if user wants to edit then put request is used
    if (editDetails) {
      Axios
      .put(`https://react-crud-e02cf.firebaseio.com/post.json`,
        {
          postName: postName === "" ? postTitle : postName,
          postDetail: postDetail === "" ? postDescription : postDetail,
          postDate: postDate === "" ? scheduleDate : postDate,
        }
      )
        .then((response) => {
          alert("post edited succesfully");
          setIsPostEdited(true);
        })
        .catch((error) => console.log("Error in editDetails" + error));
    }
    //  if user wants to add a new card
    else {
      Axios
      .post(`https://react-crud-e02cf.firebaseio.com/post.json`,
        {
          postName: postName,
          postDetail: postDetail,
          postDate: postDate,
        }
      )
        .then((response) => {
          alert("post added succesfully");
          setIsPostAdded(true);
        })
        .catch((error) => console.log("Error" + error));
    }

    setShowModal(false);
    setEditDetails(false);

    setPostName("");
    setPostDetail("");
    setPostDate("");
  }
};

 // handles edit button click
 const handleEdit = (e) => {
  setShowDetails(false);
  setShowModal(true);
  setEditDetails(true);
};


  // handles archive on card archive click
  const handleDelete = (e) => {
    Axios
    .delete(`https://react-crud-e02cf.firebaseio.com/post.json`)
      .then((response) => {
        alert("post deleted succesfully");
        setIsPostDelete(true);
      })
      .catch((error) => console.log("Error" + error));

    setShowDetails(false);
  };



  const  modalCloseHandler = () => { setShowModal(false);};

  // const  showmodalCloseHandler = () => {setShowDetails(false)};

  //modal of Add and Edit Card
  let modalContent = showModal ? 

  (
      <>
    <div className="container py-4">
      <div className="row">
        <div className="col-md-12 text-left">
          <h3>{editDetails ? 'Edit Post' : 'Add Post'}</h3>
          {formIncompleteError ? <p style={{color : 'red'}}>Kindly complete the form before adding Column</p> : null}
        <div className="form-group">
          <label for="postTitle">Post Title</label>
          <input type="text" id="post_title" defaultValue={editDetails ? postTitle : ""} placeholder="Enter a column name" ref={postTitles} onChange={(event) => setPostTitle(event.target.value)} className="form-control"/>
        </div> 
        <div className="form-group">
          <label for="postDescription">Post Description</label>
          <input type="text" id="post_description" defaultValue={editDetails ? postDescription : ""} placeholder="Enter a column name" ref={postDescriptions} onChange={(event) => setPostDescription(event.target.value)} className="form-control"/>
        </div> 
        <div className="form-group">
          <label for="schedule">Schedule Date</label>
          <input type="date" id="schedule_date" defaultValue={editDetails ? scheduleDate : ""} placeholder="Enter a column name" ref={scheduleDates} onChange={(event) => setScheduleDate(event.target.value)} className="form-control"/>
        </div>
        <button disabled={formComplete} className="btn btn-info" onClick={handleAddPostData}>{editDetails ? 'Edit Post' : 'Add Post'}</button>
        </div>
      </div>
    </div>


      </>
  )
  :null;

  // //modal of show card details
  // let showmodalContent = showDetails ? 

  // (
  //     <>

  //   <div className="container py-4">
  //     <div className="row">
  //       <div className="col-md-12 text-left">
  //         <h3>Edit Post</h3>
  //       <div className="form-group">
  //         <label for="postTitle">Post Title</label>
  //         <input type="text" className="form-control"/>
  //       </div> 
  //       <div class="form-group">       
  //           <label for="attach_img">Attach Image</label>
  //             <div class="custom-file mb-3">
  //             <input type="file" class="custom-file-input" id="attach_img" name="attach_img"/>
  //             <label class="custom-file-label" for="attach_img">Choose file</label>
  //       </div>
  //       </div>
  //       <div className="form-group">
  //         <label for="schedule">Schedule Date</label>
  //         <input type="date" className="form-control"/>
  //       </div>
  //       <button type="submit" className="btn btn-success">Edit Post</button>
  //       </div>
  //     </div>
  //   </div>

  //     </>
  // )
  // :null;

  return (
    <div className="wrapper d-flex align-items-stretch">
    <Sidebar />
    <div className="container py-3 post-data">
      <h2>Posts</h2>
      <button className="btn btn-dark mt-4" onClick={() => setShowModal(true)}>Add Post</button>
      <div className="post-table my-5">
      <table className="table table-striped table-hover table-dark">
      <thead>
        <tr>
          <th scope="col">Post Title</th>
          <th scope="col">Post Description</th>
          <th scope="col">Schedule date</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
      <tbody>
      {postData ?
        Object.entries(postData).map((item) => (
        <tr>
          <td>{postName}</td>
          <td>{postDetail}</td>
          <td>{postDate}</td>
          <td><button type="button" onClick={() => setShowDetails(true)} class="btn btn-info btn-sm"><i class="fa fa-pencil" aria-hidden="true"></i></button><button type="button" class="btn btn-danger btn-sm ml-2"><i class="fa fa-trash" aria-hidden="true"></i></button></td>
        </tr>
        ) ) : <div className="noTaskAdded">No tasks added</div>}
      </tbody>
    </table>
      </div>
        <br />
        {showModal && (

            <Modal  
                content = {modalContent}
                close = {modalCloseHandler}/> 
        )}

       {/* {showDetails && (

        <Modal  
            content = {showmodalContent}
            close = {showmodalCloseHandler}/> 
    )} */}

    </div>
    </div>

)
}

export default Home;
